import React, { useState } from "react";
import { Link } from "react-router-dom";
import { Gear, ShoppingCart } from "phosphor-react";
import "./navbar.css";

export const Navbar = () => {
  const [showAccessibilityMenu, setShowAccessibilityMenu] = useState(false);
  return (
    <div className="navbar">
  <div className="links">
    {/* Add the accessibility icon */}
    <div className="accessibilityIcon" onClick={() => setShowAccessibilityMenu(!showAccessibilityMenu)}>
      <PersonArmsSpread size={32} />
    </div>
    {/* Display the accessibility dropdown menu if showAccessibilityMenu is true */}
    {showAccessibilityMenu && (
      <div className="accessibilityMenu">
        <ul>
          <li>Dyslexia Friendly Text</li>
          <li>Alternative Text</li>
          <li>Audio Queue</li>
          <li>Change Font Size</li>
          <li>Contrast Changing</li>
        </ul>
      </div>
    )}
    <Link to="/"> Shop </Link>
    <Link to="/contact"> Contact </Link>
    <Link to="/cart">
      <Gear size={32} />
    </Link>
  </div>
</div>
  );
};
