import React, { useState } from "react";
import { Link } from "react-router-dom";
import { ShoppingCart, Wheelchair } from "phosphor-react";
import "./navbar.css";

export const Navbar = () => {
  const [showAccessibilityMenu, setShowAccessibilityMenu] = useState(false);

  const toggleAccessibilityMenu = () => {
    setShowAccessibilityMenu(!showAccessibilityMenu);
  };

  return (
    <div className="navbar">
      <div className="links">
        {/* Accessibility Icon */}
        <Wheelchair size={32} onClick={toggleAccessibilityMenu} />

        {/* Other existing links */}
        <Link to="/"> Shop </Link>
        <Link to="/contact"> Contact </Link>
        <Link to="/cart">
          <ShoppingCart size={32} />
        </Link>
      </div>

      {/* Accessibility menu */}
      {showAccessibilityMenu && (
        <div className="accessibility-menu">
          {/* Place your accessibility features here */}
        </div>
      )}
    </div>
  );
};
