import React, { useContext } from "react";
import { ShopContext } from "../../context/shop-context";
import useSound from "use-sound";
import cartActionSound from "../../assets/cart-action.mp3";

export const CartItem = (props) => {
  const { id, productName, price, productImage } = props.data;
  const { cartItems, addToCart, removeFromCart, updateCartItemCount } =
    useContext(ShopContext);
  const [play] = useSound(cartActionSound);

  return (
    <div className="cartItem">
      <img src={productImage} alt={`Product image of ${productName}`} />
      <div className="description">
        <p>
          <b>{productName}</b>
        </p>
        <p> Price: ${price}</p>
        <div className="countHandler">
          <button onClick={() => { removeFromCart(id); play(); }}> - </button>
          <input
            value={cartItems[id]}
            onChange={(e) => updateCartItemCount(Number(e.target.value), id)}
          />
          <button onClick={() => { addToCart(id); play(); }}> + </button>
        </div>
      </div>
    </div>
  );
};
